INSERT INTO `post` (`id`, `title`, `content`, `createddate`, `createdBy`, `category_id`, `image`, `description`) VALUES
(1, 'Tế bào gốc: sự kì diệu hiện diện trong cơ thể bạn', '<p>Mỗi ng&agrave;y mỗi người rụng 50-125 sợi t&oacute;c, nhưng số t&oacute;c của bạn vẫn duy tr&igrave; ổn định qua nhiều năm (trừ những người gi&agrave; hay bị h&oacute;i).</p>\r\n<p>Mỗi giờ mỗi người mất khoảng 30.000-40.000 tế b&agrave;o da, m&agrave; ch&uacute;ng ta hay gọi l&agrave; da chết, nhưng l&agrave;n da của bạn vẫn nguy&ecirc;n vẹn.</p>\r\n<p>Mỗi gi&acirc;y mỗi người mất 2.000.000 tế b&agrave;o hồng cầu, nhưng cơ thể vẫn đượ cung cấp đầy đủ oxy.</p>\r\n<p>Bạn c&oacute; bao giờ để &yacute; đến những thay đổi n&agrave;y trong cơ thể của m&igrave;nh kh&ocirc;ng? Bạn c&oacute; bao giờ tự hỏi, l&agrave;m sao cơ thể b&ugrave; đắp lại những mất m&aacute;t lớn như vậy kh&ocirc;ng? Nếu cơ thể kh&ocirc;ng thể hay giảm khả năng b&ugrave; đắp th&igrave; điều g&igrave; sẽ xảy ra với bạn?</p>\r\n<p>C&acirc;u trả lời đến từ sự k&igrave; diệu của đội ngũ tế b&agrave;o gốc hiện diện trong cơ thể bạn, kh&ocirc;ng chỉ ở t&oacute;c, ở da, hay ở m&aacute;u, m&agrave; c&ograve;n ở nhiều cơ quan kh&aacute;c trong cơ thể. Ch&uacute;ng tạo ra đ&uacute;ng số t&oacute;c bạn bị rụng, đ&uacute;ng số tế b&agrave;o da v&agrave; tế b&agrave;o hồng cầu bạn bị mất đi. Nếu &iacute;t hơn, cơ thể bạn sẽ l&atilde;o ho&aacute; nhanh. Nếu nhiều hơn, c&oacute; khả năng dẫn đến ung thư.</p>\r\n<p><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/Khi%20hoi%20tho%20hoa%20thinh%20khong/Anh%20%281%29.JPG\"></p>\r\n<p>Xa hơn nữa, c&aacute;c tế b&agrave;o gốc n&agrave;y c&oacute; thể được sử dụng trong chữa bệnh hay thẩm mỹ. C&aacute;c phương ph&aacute;p cấy gh&eacute;p tế b&agrave;o gốc đang đưa đến nhiều th&agrave;nh c&ocirc;ng v&agrave; triển vọng chữa trị nhiều loại bệnh nan y v&agrave; m&atilde;n t&iacute;nh như ung thư m&aacute;u, tiểu đường, Alzheimer, v.v... Đ&atilde; c&oacute; rất nhiều bệnh nh&acirc;n ung thư m&aacute;u được cứu sống nhờ phương ph&aacute;p cấy gh&eacute;p tuỷ xương, m&agrave; bản chất l&agrave; cấy gh&eacute;p tế b&agrave;o gốc tạo m&aacute;u. C&ograve;n rất nhiều thử nghiệm cho c&aacute;c loại bệnh kh&aacute;c đang được tiến h&agrave;nh ở khắp mọi nơi.</p>\r\n<p>Đi k&egrave;m với triển vọng l&agrave; rủi ro. Cũng đ&atilde; c&oacute; nhiều bệnh nh&acirc;n bị chết v&agrave; bị thương tổn khi sử dụng liệu ph&aacute;p tế b&agrave;o gốc.</p>\r\n<p><strong>Bạn nhận được g&igrave; khi đọc cuốn s&aacute;ch n&agrave;y?</strong></p>\r\n<p>Với sự dẫn dắt của t&aacute;c giả qua g&oacute;c nh&igrave;n rất kh&aacute;ch quan, bạn đọc sẽ thu thập nhiều kiến thức đa chiều để tự đ&aacute;nh gi&aacute; v&agrave; định hướng cho ch&iacute;nh m&igrave;nh v&agrave; người th&acirc;n khi muốn t&igrave;m một liệu ph&aacute;p tế b&agrave;o gốc khả thi nhằm cải thiện sức khỏe hay chất lượng cuộc sống.</p>\r\n<p>Thậm ch&iacute; nếu kh&ocirc;ng đủ thời gian để đi hết chiều d&agrave;i cuốn s&aacute;ch th&igrave; bạn vẫn c&oacute; thể sử dụng n&oacute; như một nguồn tư liệu để tra cứu v&agrave; t&igrave;m t&ograve;i s&acirc;u hơn khi cần.</p>\r\n<p>V&igrave; vậy, ngo&agrave;i việc chuyển ngữ v&agrave; ch&uacute; giải nội dung của cuốn s&aacute;ch, nh&oacute;m dịch c&ograve;n tạo ra một danh s&aacute;ch c&aacute;c thuật ngữ chuy&ecirc;n ng&agrave;nh Việt-Anh phổ biến, th&ocirc;ng dụng để bạn đọc c&oacute; thể tham khảo. Trong đ&oacute; c&oacute; đề xuất một v&agrave;i từ tiếng Việt mới cho những thuật ngữ tiếng Anh mới hoặc vốn &iacute;t được sử dụng trong tiếng Việt.</p>\r\n<p>Vậy bản chất của tế b&agrave;o gốc l&agrave; g&igrave;? Hiểu về tế b&agrave;o gốc gi&uacute;p &iacute;ch g&igrave; cho cuộc sống của bạn? C&oacute; n&ecirc;n sử dụng liệu ph&aacute;p tế b&agrave;o gốc cho thẩm mỹ v&agrave; trị bệnh kh&ocirc;ng? Nếu phải sử dụng, những điều g&igrave; n&ecirc;n được biết trước, được hiểu r&otilde; để tự bảo vệ bản th&acirc;n? ... Bạn sẽ t&igrave;m thấy lời giải đ&aacute;p cho những c&acirc;u hỏi n&agrave;y trong cuốn s&aacute;ch Tế b&agrave;o gốc: Kh&aacute;m ph&aacute; c&ugrave;ng nh&agrave; khoa học được dịch ra từ bản gốc &ldquo;Stem cells: an insider&rsquo;s guide&rdquo; của TS Paul Knoepfler.</p>', '2022-05-29 22:20:25', 'user1', 3, 'images/3936.jpg', 'Tế bào gốc là một cuốn sách mới và rất hấp dẫn, đưa người đọc vào thế giới tế bào gốc thông qua sự hướng dẫn của một chuyên gia ở tầm quốc tế – TS Paul Knoepfler.'),
(5, 'Việt sử: Xứ Đàng Trong 1558 - 1777: Liệu Việt Nam có là một quốc gia ôn hòa như ta vẫn nghĩ?', '<p>Việt Nam, khởi thủy xuất ph&aacute;t chỉ l&agrave; một l&atilde;nh thổ nhỏ b&eacute; nằm về ph&iacute;a Nam Trung Quốc. Trải qua h&agrave;ng ng&agrave;n năm h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, l&atilde;nh thổ Việt Nam ng&agrave;y c&agrave;ng mở rộng về ph&iacute;a Nam. Xu hướng mở mang bờ c&otilde;i n&agrave;y xuất ph&aacute;t từ hai nguy&ecirc;n nh&acirc;n ch&iacute;nh:&nbsp;<em>triết l&iacute; sức mạnh v&agrave; địa ch&iacute;nh học.</em></p>\r\n<p>Với mục đ&iacute;ch ch&iacute;nh trị &ndash; kinh tế, khi tiến h&agrave;nh qu&aacute; tr&igrave;nh mở mang l&atilde;nh thổ v&agrave; tăng cường sức mạnh của m&igrave;nh, Việt Nam đ&atilde; kh&ocirc;ng thể tiến l&ecirc;n ph&iacute;a Bắc v&igrave; c&oacute; Trung Quốc h&ugrave;ng mạnh. V&ocirc; h&igrave;nh trung, Việt Nam kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c l&agrave; phải chống lại qu&aacute; tr&igrave;nh mở mang l&atilde;nh thổ về ph&iacute;a Nam của Trung Quốc v&agrave; kết quả l&agrave; Việt Nam đ&atilde; phải trải qua một qu&aacute; tr&igrave;nh lịch sử đấu tranh l&acirc;u d&agrave;i.</p>\r\n<p>Mặt kh&aacute;c, Việt Nam cũng đ&atilde; mấy lần thử tiến về ph&iacute;a T&acirc;y l&agrave; nước L&agrave;o với mục đ&iacute;ch v&agrave; nguy&ecirc;n nh&acirc;n như đ&atilde; n&ecirc;u tr&ecirc;n nhưng giữa hai nước c&oacute; d&atilde;y n&uacute;i d&agrave;i hiểm trở n&ecirc;n kh&ocirc;ng được như mong muốn, thậm ch&iacute; c&ograve;n mất đi một phần l&atilde;nh thổ về tay L&agrave;o v&agrave; c&aacute;c nước l&aacute;ng giềng ph&iacute;a T&acirc;y Nam. Kết quả l&agrave; Việt Nam v&agrave; L&agrave;o tiếp tục duy tr&igrave; mối quan hệ hữu nghị truyền thống, bất chấp quy luật lịch sử &ldquo;<em>giữa c&aacute;c nước l&aacute;ng giềng kh&ocirc;ng c&oacute; quan hệ tốt</em>&rdquo;.</p>\r\n<p>X&eacute;t cho c&ugrave;ng, l&atilde;nh thổ Việt Nam c&oacute; cốt l&otilde;i l&agrave; v&ugrave;ng Đồng bằng ch&acirc;u thổ s&ocirc;ng Hồng, k&eacute;o d&agrave;i đến địa giới Quảng B&igrave;nh Quảng Trị b&acirc;y giờ. V&ugrave;ng đất Quảng Nam trở v&agrave;o vốn thuộc Chăm Pa. Với sự ph&aacute;t triển của nh&agrave; L&ecirc;, năm 1471, vua L&ecirc; Th&aacute;nh T&ocirc;ng mới s&aacute;p nhập th&ecirc;m v&ugrave;ng đất ph&iacute;a Bắc của Chi&ecirc;m Th&agrave;nh.</p>\r\n<p><img src=\"https://www.dkn.tv/wp-content/uploads/2017/07/vua-le-thanh-tong.jpg\" alt=\"Kết quả h&igrave;nh ảnh cho vua l&ecirc; th&aacute;nh t&ocirc;ng\"></p>\r\n<p>C&oacute; thể n&oacute;i, &ldquo;th&agrave;nh tựu&rdquo; nổi bật nhất trong việc mở mang bờ c&otilde;i nước ta đ&atilde; gọi t&ecirc;n nh&agrave; Nguyễn, v&agrave; lịch sử của &ldquo;Xứ Đ&agrave;ng Trong&rdquo; cũng chỉ được ch&iacute;nh thức</p>\r\n<p>ghi ch&eacute;p, bi&ecirc;n tập v&agrave; c&ocirc;ng nhận sau cuộc &ldquo;Nam tiến&rdquo; của nh&agrave; Nguyễn.</p>\r\n<p>Vậy c&ograve;n trước đ&oacute; th&igrave; sao?</p>\r\n<p>V&ugrave;ng đất vốn thuộc vương quốc Chăm Pa, Ch&acirc;n Lạp th&igrave; sao? Tại sao lại trở th&agrave;nh v&ugrave;ng đất của Việt Nam?</p>', '2022-05-29 22:26:21', 'user1', 1, 'images/3732.jpg', 'Suốt thời học sinh của mình, tôi luôn ngây thơ cho rằng lãnh thổ Việt Nam có được không dựa trên xâm lăng, chiếm đóng, vì những thông tin ấy vốn dĩ đâu được ghi vào sách giáo khoa. Nhưng hành trình này liệu có đơn giản như những gì được trình bày qua vài ba cuốn sách? Bản thân tôi tin rằng, không có hành trình nào không có máu và nước mắt, cũng không có cuộc phân chia tranh giành lãnh thổ nào lại chỉ diễn ra trong hòa bình.'),
(18, '2 cuốn sách kinh tế và quản trị đạt Giải Sách Hay 2020', '<p><strong>Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc</strong></p>\r\n<p><strong><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/VH/su-giau-va-ngheo-cua-cac-dan-toc%203%20780.jpg\"></strong></p>\r\n<p>Cuốn s&aacute;ch Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc</p>\r\n<p>Đạt Giải S&aacute;ch Hay 2020 hạng mục s&aacute;ch kinh tế, thể loại s&aacute;ch dịch, cuốn s&aacute;ch \"Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc\" (t&aacute;c giả David S. Landes, dịch giả Sơn Phạm &amp; Vũ Ho&agrave;ng Linh; NXB Tri thức &amp; Omega xuất bản) l&yacute; giải vấn đề gi&agrave;u ngh&egrave;o của c&aacute;c quốc gia, d&acirc;n tộc tr&ecirc;n thế giới với c&aacute;ch tiếp cận đa chiều, to&agrave;n diện, khoa học. Dựa tr&ecirc;n sự ph&acirc;n t&iacute;ch tất cả c&aacute;c yếu tố quan trọng nhất gồm tự nhi&ecirc;n, con người, sự t&aacute;c động qua lại giữa hai yếu tố n&agrave;y&hellip; t&aacute;c giả đ&atilde; l&yacute; giải được một c&aacute;ch kh&aacute;ch quan nguy&ecirc;n nh&acirc;n v&agrave; con đường dẫn đến sự hưng thịnh hay lụi bại của c&aacute;c quốc gia, d&acirc;n tộc tr&ecirc;n thế giới.</p>\r\n<p>Theo t&aacute;c giả, sự gi&agrave;u- ngh&egrave;o đ&oacute; chịu nhiều t&aacute;c động của xuất ph&aacute;t điểm (điều kiện tự nhi&ecirc;n, vị tr&iacute; địa l&yacute;, v.v). Mặt kh&aacute;c, điều đ&oacute; phụ thuộc rất lớn v&agrave;o: những phẩm chất, t&iacute;nh c&aacute;ch ở tầm d&acirc;n tộc v&agrave; c&aacute;c định chế x&atilde; hội th&iacute;ch hợp để th&uacute;c đẩy con người l&agrave;m gi&agrave;u một c&aacute;ch hiệu quả - đ&acirc;y l&agrave; những điểm bất biến m&agrave; kh&ocirc;ng quốc gia, d&acirc;n tộc gi&agrave;u c&oacute;, ph&aacute;t triển n&agrave;o thiếu vắng; đồng thời n&oacute; cũng giữ cho sự ch&ecirc;nh lệch gi&agrave;u-ngh&egrave;o kh&ocirc;ng g&acirc;y ra những bất ổn, ph&acirc;n h&oacute;a c&oacute; thể g&acirc;y khủng hoảng, thậm ch&iacute; l&agrave; dẫn tới sự sụp đổ của hệ thống x&atilde; hội cũng như quốc gia.</p>\r\n<p>Đọc cuốn s&aacute;ch \"Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc\" mỗi quốc gia, mỗi d&acirc;n tộc c&oacute; thể nh&igrave;n v&agrave;o c&aacute;c b&agrave;i học th&agrave;nh c&ocirc;ng v&agrave; thất bại của c&aacute;c quốc gia, d&acirc;n tộc kh&aacute;c tr&ecirc;n thế giới, v&agrave; biết cần trang bị cho d&acirc;n tộc m&igrave;nh những g&igrave; để c&oacute; cơ may bứt l&ecirc;n trong cuộc đua hướng tới sự phồn vinh.</p>\r\n<p><strong>Thần kỳ kinh tế T&acirc;y Đức giai đoạn 1949-1969</strong></p>\r\n<p><strong><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/VH/than-ky-kinh-te-tay-duc.jpg\"></strong></p>\r\n<p style=\"text-align: left;\">B&igrave;a 1 v&agrave; b&igrave;a 4 cuốn s&aacute;ch Thần kỳ kinh tế T&acirc;y Đức</p>\r\n<p>T&aacute;c phẩm n&agrave;y của t&aacute;c giả T&ocirc;n Thất Th&ocirc;ng đạt Giải S&aacute;ch Hay 2020 hạng mục S&aacute;ch kinh tế, thể loại s&aacute;ch viết (NXB Tri thức &amp; Phương Nam Books xuất bản). Trong cuốn s&aacute;ch, t&aacute;c giả sẽ chỉ ra những nh&acirc;n tố quan trọng h&agrave;ng đầu gi&uacute;p T&acirc;y Đức đổ n&aacute;t sau chiến tranh c&oacute; thể vươn l&ecirc;n ph&aacute;t triển ngang bằng, thậm ch&iacute; vượt trội so với nhiều nước xung quanh.</p>\r\n<p>Trong đ&oacute;, M&ocirc; h&igrave;nh Kinh tế Thị trường X&atilde; hội được T&acirc;y Đức &aacute;p dụng l&agrave; một c&ocirc;ng thức h&ograve;a giải giữa nguy&ecirc;n l&yacute; tự do tr&ecirc;n thị trường v&agrave; nguy&ecirc;n l&yacute; c&acirc;n bằng x&atilde; hội trong một khung trật tự được nh&agrave; nước x&acirc;y dựng v&agrave; bảo vệ. C&ocirc;ng thức h&ograve;a giải ấy c&oacute; khả năng kết hợp h&agrave;i h&ograve;a giữa l&yacute; tưởng b&igrave;nh đẳng, tự do con người v&agrave; tăng trưởng kinh tế trong một thế c&acirc;n bằng hợp l&yacute;&hellip; V&agrave; trong bối cảnh nền kinh tễ x&atilde; hội Việt Nam hiện tại, dường như những b&agrave;i học, kinh nghiệm ấy vẫn c&ograve;n nguy&ecirc;n gi&aacute; trị để học hỏi.</p>', '2022-05-30 23:57:12', 'user1', 2, 'images/5979.jpg', 'Lễ công bố Giải Sách Hay 2020 do Viện Giáo Dục IRED, Dự án Khuyến đọc Sách Hay và Sáng kiến OpenEdu đồng tổ chức đã diễn ra vào cuối tháng 9 vừa qua tại TP HCM. Trải qua 10 mùa giải, dù có nhiều thăng trầm, nhưng Giải Sách Hay đã tạo được tiếng vang đáng kể trong ngành sách, trở thành “bộ lọc” đáng tin cậy đối với rất nhiều độc giả yêu sách. Dưới đây là 2 cuốn sách kinh tế và quản trị đạt Giải Sách Hay hạng mục Sách Kinh tế và Sách Quản trị trong mùa giải năm nay.'),
(19, 'Tác phẩm đầy chất thơ của họa sỹ hàng đầu Xưởng phim Hoạt hình', '<p>Họa sĩ Mai Long (1930), l&agrave; một trong số 21 học vi&ecirc;n kh&oacute;a Mĩ thuật Kh&aacute;ng chiến do danh họa T&ocirc; Ngọc V&acirc;n đ&agrave;o tạo. H&ograve;a b&igrave;nh lập lại, &ocirc;ng tiếp tục theo học tại Đại học Mỹ thuật H&agrave; Nội. Từ khi c&ograve;n ngồi tr&ecirc;n ghế giảng đường, &ocirc;ng đ&atilde; tham gia vẽ cho bộ phim hoạt họa <em>Đ&ecirc;m trăng rằm</em>, sau đ&oacute;, &ocirc;ng trở th&agrave;nh họa sĩ ch&iacute;nh thực hiện bộ phim hoạt h&igrave;nh m&agrave;u đầu ti&ecirc;n của Việt Nam:&nbsp;<em>B&agrave;i ca tr&ecirc;n v&aacute;ch n&uacute;i</em>.</p>\r\n<p>&Ocirc;ng l&agrave;m họa sĩ ch&iacute;nh cho rất nhiều bộ phim, với nhiều thể loại từ d&acirc;n gian, truyền thuyết, cổ t&iacute;ch, thần thoại như:&nbsp;<em>Chuyện &ocirc;ng Gi&oacute;ng</em>&nbsp;(đạo diễn Ng&ocirc; Mạnh L&acirc;n),&nbsp;<em>Kặm Phạ - N&agrave;ng Ng&agrave;</em>&nbsp;(đạo diễn Ho&agrave;ng S&ugrave;ng),&nbsp;<em>Sơn Tinh v&agrave; Thủy Tinh</em>&nbsp;(đạo diễn Trương Qua),&nbsp;<em>&Acirc;u Cơ - Lạc Long Qu&acirc;n</em>&nbsp;(đồng bi&ecirc;n kịch với Trần Ngọc Thanh, đồng đạo diễn với Nghi&ecirc;m Dung)&hellip; Hầu hết bộ phim ấy đều đoạt giải thưởng cao tại c&aacute;c kỳ li&ecirc;n hoan phim trong nước v&agrave; quốc tế</p>\r\n<p><img src=\"http://hoathinh.org.vn/sites/default/files/anh_4.1_0.png\"></p>\r\n<p>Mai Long dần khẳng định v&agrave; trở th&agrave;nh một trong số &iacute;t họa sĩ h&agrave;ng đầu của phim hoạt họa. Trong suốt sự nghiệp của m&igrave;nh, họa sĩ Mai Long lu&ocirc;n theo đuổi c&aacute;i đẹp, chất thơ, chất l&atilde;ng mạn v&agrave; sự tưởng tượng như l&yacute; tưởng nghệ thuật.</p>\r\n<p>Sử dụng nhiều chất liệu như phấn m&agrave;u, sơn dầu, sơn m&agrave;i, lụa, nhưng &ocirc;ng đứng vững trong l&agrave;ng tranh lụa. C&aacute;c bức tranh lụa của &ocirc;ng vẽ Kim Trọng, Th&uacute;y Kiều đang gảy đ&agrave;n, h&igrave;nh ảnh những c&ocirc; g&aacute;i b&igrave;nh dị trong lao động, phong cảnh buổi s&aacute;ng sương m&ugrave;, buổi chiều nắng bảng lảng tr&ecirc;n n&uacute;i, hay phố cổ trong m&agrave;n mưa...</p>\r\n<p><img src=\"http://hoathinh.org.vn/sites/default/files/anh_4.3_0.png\"></p>\r\n<p>Kh&ocirc;ng chỉ nổi tiếng ở mảng phim hoạt họa, tranh lụa, họa sĩ Mai Long c&ograve;n được biết tới với những t&aacute;c phẩm đồ họa s&aacute;ch b&aacute;o. &Ocirc;ng gặt h&aacute;i nhiều th&agrave;nh c&ocirc;ng khi minh họa truyện tranh d&acirc;n gian lịch sử. Từ những năm 1970, khi kỹ thuật in ấn minh họa của Việt Nam chưa cao, c&aacute;c cuốn s&aacute;ch do Mai Long vẽ như&nbsp;<em>Tấm C&aacute;m</em>,&nbsp;<em>Sơn Tinh - Thủy Tinh</em>&nbsp;được c&aacute;c tổ Thụy Điển, người Việt tại Ph&aacute;p chọn in m&agrave;u như qu&agrave; tặng bạn đọc thiếu nhi Việt Nam.</p>\r\n<p>Nh&agrave; ph&ecirc; b&igrave;nh nghệ thuật Ekaterina Chelaeva (Nga) viết: &ldquo;&hellip; Dưới con mắt họa sĩ Mai Long, mọi c&aacute;i đều c&oacute; vẻ đẹp ri&ecirc;ng, một vẻ đẹp m&agrave; những tr&aacute;i tim băng gi&aacute; kh&ocirc;ng bao giờ cảm nhận được. Những s&aacute;ng t&aacute;c của &ocirc;ng l&agrave; sự kết hợp giữa truyền thống phương Đ&ocirc;ng s&acirc;u sắc v&agrave; t&iacute;nh hiện đại của một nghệ thuật đi&ecirc;u luyện, độc đ&aacute;o, với b&uacute;t ph&aacute;p hết sức trữ t&igrave;nh v&agrave; ph&oacute;ng kho&aacute;ng. Vẻ đẹp của thế giới xung quanh v&agrave; t&igrave;nh y&ecirc;u của Mai Long đối với thế giới n&agrave;y, đ&oacute; ch&iacute;nh l&agrave; bản chất nghệ thuật của &ocirc;ng&rdquo;.</p>', '2022-05-31 08:48:35', 'user1', 1, 'images/malong.jpg', 'Cuốn sách “Họa sĩ Mai Long - Những bức tranh như những bài thơ” giúp hình dung nét tiêu biểu về cuộc đời và sự nghiệp, những quan niệm, dấu ấn nghệ thuật của họa sĩ tài hoa.'),
(20, '3 cuốn sách kinh tế và quản trị của Alpha Books đạt Giải Sách Hay 2010 - 2020', '<p>C&ocirc;ng ty S&aacute;ch Alpha - Alpha Books ra đời với sứ mệnh chuyển giao tri thức kinh tế, quản trị kinh doanh kinh điển, ti&ecirc;n tiến tr&ecirc;n thế giới cho người Việt; g&oacute;p phần th&uacute;c đẩy sự ph&aacute;t triển của c&aacute;c doanh nghiệp, cộng đồng khởi nghiệp n&oacute;i chung v&agrave; c&aacute;c c&aacute; nh&acirc;n hoạt động trong lĩnh vực kinh tế, kinh doanh n&oacute;i ri&ecirc;ng. Đến nay, Alpha Books đ&atilde; trở th&agrave;nh thương hiệu h&agrave;ng đầu tại thị trường Việt Nam trong mảng s&aacute;ch kinh tế, quản trị kinh doanh. Kh&ocirc;ng chỉ được độc giả đ&oacute;n nhận, s&aacute;ch của Alpha Books cũng được c&aacute;c chuy&ecirc;n gia đ&aacute;nh gi&aacute; cao, v&agrave; đạt được c&aacute;c giải thưởng đ&aacute;ng ghi nhận như Giải S&aacute;ch Hay, s&aacute;ch b&aacute;n chạy nhất tr&ecirc;n thị trường&hellip;</p>\r\n<p>L&agrave; một giải thưởng độc lập v&agrave; uy t&iacute;n,&nbsp;<em>Giải S&aacute;ch Hay hạng mục s&aacute;ch Kinh tế&nbsp;</em>được trao cho c&aacute;c cuốn&nbsp;s&aacute;ch c&oacute; gi&aacute; trị g&oacute;p phần định h&igrave;nh nền tảng về khoa học kinh tế ở Việt Nam v&agrave;/hoặc mang lại tư tưởng, tinh thần, nhận thức mới, c&oacute; t&iacute;nh đột ph&aacute; về c&aacute;c vấn đề kinh tế của Việt Nam, thế giới.</p>\r\n<p><em>Giải S&aacute;ch Hay hạng mục s&aacute;ch Quản trị&nbsp;</em>được trao cho c&aacute;c cuốn&nbsp;s&aacute;ch c&oacute; gi&aacute; trị g&oacute;p phần định h&igrave;nh nền tảng về khoa học quản trị/kinh doanh ở Việt Nam v&agrave;/hoặc mang lại tư tưởng, tinh thần, nhận thức mới, c&oacute; t&iacute;nh đột ph&aacute; về c&aacute;c vấn đề quản trị/kinh doanh của Việt Nam, thế giới.</p>\r\n<p>Dưới đ&acirc;y, Trạm đọc sẽ giới thiệu ngắn gọn nội dung những cuốn s&aacute;ch Kinh tế v&agrave; Quản trị của Alpha Books đạt Giải S&aacute;ch Hay từ năm 2010 đến 2020.</p>\r\n<ol>\r\n<li><strong>Quốc gia khởi nghiệp</strong></li>\r\n</ol>\r\n<p><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/123/1.jpg\"></p>\r\n<p>Cuốn s&aacute;ch Quốc gia khởi nghiệp (T&aacute;c giả Dan Senor &amp; Saul Singer, Dịch giả Tr&iacute; Vương, Đơn vị xuất bản l&agrave; Alpha Books &amp; NXB Thế giới) đạt Giải S&aacute;ch Hay năm 2014. Cuốn s&aacute;ch l&agrave; c&acirc;u chuyện về nền kinh tế thần kỳ của Israel. Nếu bạn đang c&oacute; thắc mắc rằng: l&agrave;m thế n&agrave;o để một đất nước b&eacute; nhỏ lu&ocirc;n phải đối ph&oacute; với sự th&ugrave; địch của c&aacute;c quốc gia l&acirc;n cận, m&agrave; vẫn trở th&agrave;nh một quốc gia khởi nghiệp, s&aacute;ng tạo nổi bật tr&ecirc;n c&aacute;c phương diện về c&ocirc;ng nghệ, qu&acirc;n sự v&agrave; d&acirc;n sự h&agrave;ng đầu tr&ecirc;n thế giới, th&igrave; cuốn s&aacute;ch n&agrave;y sẽ phần n&agrave;o l&yacute; giải đầy đủ c&aacute;c thắc mắc đ&oacute; của bạn.</p>\r\n<p>Quốc gia khởi nghiệp cũng l&agrave; một cuốn s&aacute;ch tham khảo tuyệt vời cho c&aacute;c chuy&ecirc;n gia v&agrave; tất cả những ai quan t&acirc;m đến việc th&uacute;c đẩy, ph&aacute;t triển Việt Nam trở th&agrave;nh một quốc gia khởi nghiệp th&agrave;nh c&ocirc;ng.</p>\r\n<p><strong>2. Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc</strong></p>\r\n<p><strong><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/123/2.jpg\"></strong></p>\r\n<p>Cuốn s&aacute;ch Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc (T&aacute;c giả: Davids. Landes, Dịch giả: Sơn Phạm &amp; Vũ Ho&agrave;ng Linh; Đơn vị xuất bản: NXB Tri thức v&agrave; Omega+) đạt Giải S&aacute;ch Hay năm 2020. T&aacute;c phẩm kinh điển n&agrave;y sẽ đưa người đọc đến gốc rễ tạo sự gi&agrave;u ngh&egrave;o của c&aacute;c d&acirc;n tộc tr&ecirc;n thế giới, soi chiếu từ cả hai g&oacute;c độ về kinh tế học v&agrave; lịch sử. Sự gi&agrave;u v&agrave; ngh&egrave;o của c&aacute;c d&acirc;n tộc chắc hẳn sẽ l&agrave; t&agrave;i liệu hữu &iacute;ch về lĩnh vực khoa học x&atilde; hội nếu như độc giả quan t&acirc;m đến những th&agrave;nh c&ocirc;ng lớn, thất bại lớn trong nền kinh tế thế giới, hay b&agrave;i học kinh nghiệm cho c&aacute;c quốc gia đang ph&aacute;t triển.</p>\r\n<p><strong>3. Chiến lược đại dương xanh</strong></p>\r\n<p><strong><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/123/3.jpg\"></strong></p>\r\n<p>Cuốn s&aacute;ch Chiến lược đại dương xanh (T&aacute;c giả: W. Chan Kim &amp; Renee Mauborgne, Dịch giả: Phương Th&uacute;y, Đơn vị xuất bản: NXB Tri thức &amp; Alpha Books) đạt Giải S&aacute;ch Hay năm 201 thể loại s&aacute;ch dịch. Chiến lược đại dương xanh giới thiệu với độc giả, đặc biệt l&agrave; c&aacute;c doanh nh&acirc;n, c&aacute;c nh&agrave; l&atilde;nh đạo doanh nghiệp một chiến lược cạnh tranh ho&agrave;n to&agrave;n mới: ph&aacute;t triển v&agrave; mở rộng một thị trường trong đ&oacute; kh&ocirc;ng c&oacute; cạnh tranh hoặc sự cạnh tranh l&agrave; kh&ocirc;ng cần thiết m&agrave; c&aacute;c c&ocirc;ng ty c&oacute; thể kh&aacute;m ph&aacute; v&agrave; khai th&aacute;c.</p>\r\n<p>Đọc cuốn s&aacute;ch độc giả sẽ nắm được n&oacute;i về bản chất của Chiến lược đại dương xanh l&agrave; n&acirc;ng cao c&aacute;c gi&aacute; trị đi k&egrave;m sự tiện lợi, gi&aacute; th&agrave;nh thấp v&agrave; giảm chi ph&iacute;. V&agrave; hơn hết, chiến lược n&agrave;y c&ograve;n buộc c&aacute;c c&ocirc;ng ty phải c&oacute; bước nhảy vọt về gi&aacute; trị v&agrave; mang lại sự tăng trưởng mạnh mẽ gi&aacute; trị cho cả người mua v&agrave; của ch&iacute;nh họ nữa.</p>', '2022-05-31 08:53:28', 'user1', 2, 'images/6013.jpg', 'Lễ công bố Giải Sách Hay 2020 do Viện Giáo Dục IRED, Dự án Khuyến đọc Sách Hay và Sáng kiến OpenEdu đồng tổ chức đã diễn ra vào cuối tháng 9 vừa qua tại TP HCM. Trải qua 10 mùa giải, dù có nhiều thăng trầm, nhưng Giải Sách Hay đã tạo được tiếng vang đáng kể trong ngành sách, trở thành “bộ lọc” đáng tin cậy đối với rất nhiều độc giả yêu sách. Dưới đây là 3 cuốn sách kinh tế và quản trị đạt Giải Sách Hay hạng mục Sách Kinh tế và Sách Quản trị trong mùa giải năm nay.'),
(21, 'Homo Deus - Lược sử tương lai: Dữ liệu sẽ phá hủy tự do của con người như thế nào?', '<p>Trọng t&acirc;m của cuốn s&aacute;ch n&agrave;y l&agrave; một &yacute; tưởng tuy đơn giản nhưng lạnh l&ugrave;ng: <strong>bản chất con người sẽ được biến đổi trong thế kỷ 21 bởi v&igrave; tr&iacute; th&ocirc;ng minh đ&atilde; được t&aacute;ch rời khỏi &yacute; thức</strong>. Ch&uacute;ng ta sẽ kh&ocirc;ng s&aacute;ng tạo n&ecirc;n những cỗ m&aacute;y c&oacute; cảm gi&aacute;c m&agrave; chỉ đơn giản l&agrave; &yacute; thức m&agrave; th&ocirc;i. Robot sẽ kh&ocirc;ng y&ecirc;u nhau (điều đ&oacute; kh&ocirc;ng c&oacute; nghĩa l&agrave; ch&uacute;ng ta kh&ocirc;ng c&oacute; khả năng y&ecirc;u robot). Nhưng ch&uacute;ng ta đ&atilde; chế tạo ra những thứ m&aacute;y m&oacute;c - những mạng lưới xử l&yacute; dữ liệu rộng lớn - c&oacute; thể biết được cảm x&uacute;c của ch&uacute;ng ta tốt hơn l&agrave; ch&uacute;ng ta biết về ch&iacute;nh m&igrave;nh: v&agrave; đ&oacute; l&agrave; tr&iacute; th&ocirc;ng minh. C&ocirc;ng cụ t&igrave;m kiếm Google kh&ocirc;ng c&oacute; niềm tin v&agrave; mong muốn của ri&ecirc;ng m&igrave;nh, n&oacute; kh&ocirc;ng quan t&acirc;m những g&igrave; ch&uacute;ng ta t&igrave;m kiếm v&agrave; n&oacute; sẽ kh&ocirc;ng cảm thấy bị tổn thương bởi h&agrave;nh vi của ch&uacute;ng ta. Nhưng Google c&oacute; thể xử l&yacute; h&agrave;nh vi của ch&uacute;ng ta để biết những g&igrave; ch&uacute;ng ta muốn trước cả khi ta biết m&igrave;nh muốn g&igrave;. Thực tế đ&oacute; c&oacute; tiềm năng thay đổi &yacute; nghĩa của con người.</p>\r\n<p>rong cuốn s&aacute;ch trước đ&acirc;y của Yuval Noah Harari, cuốn s&aacute;ch b&aacute;n chạy nhất to&agrave;n cầu&nbsp;<em>Sapiens (Sapiens - Lược sử lo&agrave;i người, Omega+)</em>, đ&atilde; đặt ra 75.000 năm lịch sử nh&acirc;n loại cuối c&ugrave;ng để nhắc nhở ch&uacute;ng ta rằng kh&ocirc;ng c&oacute; g&igrave; đặc biệt hay cần thiết về việc ch&uacute;ng ta l&agrave; ai. Ch&uacute;ng ta chỉ l&agrave; những điều ngẫu nhi&ecirc;n. Homo sapiens chỉ l&agrave; một trong những c&aacute;ch khả dĩ để trở th&agrave;nh con người, một sự đột ph&aacute; tiến h&oacute;a giống như mọi sinh vật kh&aacute;c tr&ecirc;n h&agrave;nh tinh. Cuốn s&aacute;ch kết th&uacute;c với &yacute; nghĩ rằng c&acirc;u chuyện về Homo sapiens c&oacute; thể sắp kết th&uacute;c. Ch&uacute;ng ta đang ở đỉnh cao sức mạnh nhưng ch&uacute;ng ta cũng c&oacute; thể đ&atilde; đạt đến giới hạn của n&oacute;.&nbsp;<strong>Cuốn s&aacute;ch&nbsp;<em>Homo Deus</em>&nbsp;đ&atilde; một lần nữa đ&agrave;o s&acirc;u v&agrave;o &yacute; kiến n&agrave;y để giải th&iacute;ch l&agrave;m thế n&agrave;o khả năng kiểm so&aacute;t thế giới xung quanh ch&uacute;ng ta lại đang biến ch&uacute;ng ta th&agrave;nh một thứ sinh vật mới.</strong></p>\r\n<p>Bằng chứng về quyền lực của con người ở khắp mọi nơi: ch&uacute;ng ta kh&ocirc;ng chỉ đơn thuần l&agrave; chinh phục thi&ecirc;n nhi&ecirc;n m&agrave; c&ograve;n bắt đầu đ&aacute;nh bại cả những kẻ th&ugrave; tồi tệ nhất của nh&acirc;n loại. Chiến tranh ng&agrave;y c&agrave;ng trở n&ecirc;n lỗi thời; nạn đ&oacute;i rất hiếm; bệnh dịch đ&atilde; dần r&uacute;t lui tr&ecirc;n khắp thế giới. Ch&uacute;ng ta đ&atilde; đạt được những chiến thắng n&agrave;y bằng c&aacute;ch x&acirc;y dựng những mạng lưới phức tạp hơn bao giờ hết bằng c&aacute;ch coi con người như những đơn vị th&ocirc;ng tin. Khoa học tiến h&oacute;a dạy ta rằng, theo một nghĩa n&agrave;o đ&oacute;, ta chẳng l&agrave; g&igrave; ngo&agrave;i c&aacute;c m&aacute;y xử l&yacute; dữ liệu: con người cũng l&agrave; c&aacute;c thuật to&aacute;n. Bằng c&aacute;ch thao t&aacute;c dữ liệu, ch&uacute;ng ta c&oacute; thể điều khiển số phận của m&igrave;nh. Vấn đề l&agrave; c&aacute;c thuật to&aacute;n kh&aacute;c - những thuật to&aacute;n (theo nghĩa đen) m&agrave; ch&uacute;ng ta đ&atilde; x&acirc;y dựng - c&oacute; thể l&agrave;m n&oacute; hiệu quả hơn nhiều so với ch&uacute;ng ta c&oacute; thể. Đ&oacute; l&agrave; những g&igrave; Harari n&oacute;i về việc \"t&aacute;ch rời\" tr&iacute; th&ocirc;ng minh v&agrave; &yacute; thức. Cuộc sống hiện đại được x&acirc;y dựng dựa tr&ecirc;n &yacute; tưởng rằng mỗi c&aacute; nh&acirc;n con người l&agrave; nguồn gốc của &yacute; nghĩa cũng như quyền lực. Con người rồi sẽ trở th&agrave;nh những kẻ tự quyết định điều g&igrave; xảy ra với ch&iacute;nh m&igrave;nh: đi bầu cử, mua b&aacute;n, y&ecirc;u đương. Nhưng điều đ&oacute; kh&ocirc;ng c&ograve;n nữa.</p>\r\n<p><img src=\"https://i.pinimg.com/736x/cf/84/6f/cf846f380d9f0cf66641119b4a74b173--cyborg-art-pencil-drawings.jpg\" alt=\"Related image\"></p>\r\n<p>Những điều n&agrave;y thực ra kh&ocirc;ng c&ograve;n qu&aacute; mới mẻ nữa. M&ocirc; h&igrave;nh nh&agrave; nước hiện đại tồn tại khoảng 400 năm nay thực sự cũng chỉ l&agrave; một m&aacute;y xử l&yacute; dữ liệu kh&aacute;c. Năm 1651, Nh&agrave; triết học Thomas Hobbes đ&atilde; gọi n&oacute; l&agrave;&nbsp;<em>&ldquo;automaton&rdquo;</em>&nbsp;(cũng ch&iacute;nh l&agrave; c&aacute;i m&agrave; ch&uacute;ng ta gọi l&agrave; robot). Đặc t&iacute;nh robot đ&oacute; l&agrave; nguồn sức mạnh nhưng đồng thời cũng l&agrave; sự v&ocirc; cảm: ch&iacute;nh quyền kh&ocirc;ng c&oacute; lương t&acirc;m, điều n&agrave;y cho ph&eacute;p họ đ&ocirc;i l&agrave;m những điều đ&aacute;ng sợ nhất. C&oacute; chăng những g&igrave; thay đổi hiện nay chỉ l&agrave; m&aacute;y m&oacute;c đ&atilde; trở n&ecirc;n hiệu quả hơn nhiều so với ch&iacute;nh quyền. Đ&oacute; cũng ch&iacute;nh l&agrave; điều Harari đ&atilde; chỉ ra trong t&aacute;c phẩm của m&igrave;nh:&nbsp;<strong>Ch&iacute;nh phủ nhận ra rằng việc bắt kịp với tiến bộ c&ocirc;ng nghệ gần như l&agrave; điều kh&ocirc;ng tưởng.</strong>&nbsp;X&atilde; hội hiện đại th&igrave; gần như ngược lại: mọi thứ đang chuyển động qu&aacute; nhanh đến mức kh&ocirc;ng thể tưởng tượng được tương lai c&oacute; thể sẽ xoay chuyển như thế n&agrave;o. V&agrave;o năm 1800, người ta c&oacute; thể suy nghĩ một c&aacute;ch tương đối về thế giới của năm 1900 v&agrave; c&aacute;ch ch&uacute;ng ta sống trong x&atilde; hội đ&oacute;. Đ&oacute; l&agrave; lịch sử: một chuỗi c&aacute;c sự kiện m&agrave; con người đ&oacute;ng vai tr&ograve; chủ đạo. Nhưng thế giới của năm 2100 hiện nay gần như kh&ocirc;ng thể tưởng tượng nổi. Ch&uacute;ng ta kh&ocirc;ng biết rồi m&igrave;nh sẽ ở đ&acirc;u, nếu l&uacute;c ấy ta c&ograve;n tồn tại. Ch&uacute;ng ta thậm ch&iacute; c&oacute; thể đang x&acirc;y dựng một thế giới kh&ocirc;ng c&oacute; chỗ cho ch&iacute;nh m&igrave;nh.</p>', '2022-05-31 09:00:44', 'user1', 3, 'images/4121.jpg', 'Đây có thể là một viễn cảnh thật sự đáng sợ, nhưng Trí tuệ nhân tạo (AI) mà chúng ta đang phát triển có thể làm thay đổi cả bản chất của con người - đó là một lập luận đầy hấp dẫn của Yuval Noah Harari trong cuốn sách nối tiếp của siêu phẩm Sapiens: Homo Deus - Lược sử Tương lai.'),
(22, 'Khi tiền lên mạng: Tại sao mọi người lại bỏ lỡ phát minh quan trọng nhất 500 năm qua', '<div id=\"start_char_id\" class=\"start_char\">N&oacute; mang t&iacute;nh c&aacute;ch mạng c&ograve;n hơn cả Internet, m&aacute;y xe sợi b&ocirc;ng (nd:c&aacute;ch mạng c&ocirc;ng nghiệp dệt may tại Anh cuối tk18), động cơ hơi nước, m&aacute;y vi t&iacute;nh v&agrave; điện thoại th&ocirc;ng minh kết hợp lại.</div>\r\n<p>Nếu ta xem x&eacute;t h&agrave;ng trăm năm đ&atilde; qua, th&igrave; chỉ c&oacute; ph&aacute;t minh về b&aacute;o in v&agrave; Internet l&agrave; đem lại t&aacute;c động lớn nhất cho nh&acirc;n loại. Internet v&agrave; b&aacute;o in đ&atilde; cho ph&eacute;p ch&uacute;ng ta d&acirc;n chủ ho&aacute; th&ocirc;ng tin; c&ugrave;ng với đ&oacute; l&agrave; sự phổ cập v&agrave; n&acirc;ng cao kiến thức đến người d&acirc;n tr&ecirc;n to&agrave;n thế giới.</p>\r\n<p>Vậy t&ocirc;i đang n&oacute;i về c&aacute;i g&igrave; đ&acirc;y? Ijiri đ&atilde; tạo ra thứ g&igrave; m&agrave; đ&aacute;ng kinh ngạc đến vậy?</p>\r\n<p>Đ&oacute; ch&iacute;nh l&agrave; Kế to&aacute;n Tam-ph&acirc;n. (triple entry accounting)</p>\r\n<p>Hả, c&aacute;i g&igrave;?</p>\r\n<p>Bạn kh&ocirc;ng nghe lộn đ&acirc;u, t&ocirc;i n&oacute;i nghi&ecirc;m t&uacute;c đấy.</p>\r\n<p>D&ugrave; sao cũng đừng cảm thấy xấu hổ nếu bạn đang ng&aacute;y kh&ograve; kh&ograve; rồi bỏ lỡ cuộc c&aacute;ch mạng ấy. N&oacute; đ&atilde; kh&ocirc;ng được chiếu l&ecirc;n VTV hoặc đăng tr&ecirc;n Kenh14.vn. Khi gi&aacute;o sư Ijiri qua đời v&agrave;o năm 2017, hầu hết mọi người đều chẳng biết đến c&aacute;o ph&oacute; của &ocirc;ng. Cuốn s&aacute;ch nổi tiếng nhất của &ocirc;ng, Kế to&aacute;n Động lượng v&agrave; Sổ S&aacute;ch Ba-b&ecirc;n, nhận được 0 reviews tr&ecirc;n Goodreads (nd: trang web giới thiệu, đ&aacute;nh gi&aacute; s&aacute;ch). V&igrave; vậy, kh&ocirc;ng phải chỉ m&igrave;nh bạn l&agrave; đ&atilde; bỏ lỡ n&oacute;.</p>\r\n<p>H&atilde;y thử Goole từ kh&oacute;a &ldquo;Kế to&aacute;n Tam-ph&acirc;n&rdquo; xem.</p>\r\n<p>4970 kết quả, với c&aacute;i thời của Google như hiện tại th&igrave; như vậy chẳng kh&aacute;c g&igrave; l&agrave; kh&ocirc;ng tồn tại. Những người chỉ mới xuất hiện tr&ecirc;n Facebook hay Youtube tuần trước c&ograve;n cho ra kết quả nhiều hơn thế.</p>\r\n<p>Thử coi tiếp xem, đường link đầu ti&ecirc;n l&agrave; tới Wikipedia, m&agrave; mới chỉ ở dạng sơ khai. Thứ tư xuống l&agrave; một b&agrave;i b&aacute;o từ năm 2005, Ijiri thậm ch&iacute; c&ograve;n kh&ocirc;ng được đề cập ở trang nhất.</p>\r\n<p>Vậy tại sao n&oacute; lại quan trọng đến thế nếu chẳng ai biết về n&oacute;?</p>\r\n<p>Bởi v&igrave; n&oacute; vẫn chưa được ứng dụng g&igrave; cho tới năm 2008, thế n&ecirc;n t&aacute;c động của n&oacute; vẫn chưa xảy đến. Nhưng bạn cứ chờ m&agrave; xem, n&oacute; sẽ trở th&agrave;nh một cơn s&oacute;ng thần c&agrave;n qu&eacute;t v&agrave; định h&igrave;nh lại mọi mặt của cuộc sống v&agrave; x&atilde; hội ch&uacute;ng ta.</p>\r\n<h5>L&agrave;m sao t&ocirc;i biết?</h5>\r\n<p>Để hiểu tại sao, bạn chỉ cần t&igrave;m hiểu sơ về chủ đề gợi cảm nhất của lịch sử. (nd: bạn đọc kh&ocirc;ng nhầm đ&acirc;u, &lsquo;gợi cảm&rsquo;, ahaha)</p>\r\n<h2><strong>KẾ TO&Aacute;N: CHỦ ĐỀ GỢI CẢM NHẤT LỊCH SỬ</strong></h2>\r\n<p>N&oacute;i thế n&agrave;y: Kh&ocirc;ng c&oacute; kế to&aacute;n, bạn sẽ kh&ocirc;ng được đọc b&agrave;i viết n&agrave;y tr&ecirc;n iPad của bạn, hoặc chạy xe đi l&agrave;m trong khi đeo tai nghe v&agrave; nghe nhạc tr&ecirc;n Zing.mp3. Kh&ocirc;ng c&oacute; kế to&aacute;n th&igrave; kh&ocirc;ng c&oacute; thương mại, kh&ocirc;ng bu&ocirc;n b&aacute;n. Nếu kh&ocirc;ng c&oacute; thương mại th&igrave; sẽ kh&ocirc;ng c&oacute; m&aacute;y bay, kh&ocirc;ng c&oacute; t&agrave;u hỏa, m&aacute;y k&eacute;o, kh&ocirc;ng c&oacute; động cơ hơi nước, kh&ocirc;ng c&oacute; t&ograve;a nh&agrave; chọc trời hay m&aacute;y vi t&iacute;nh. Sẽ kh&ocirc;ng c&oacute; quốc gia, kh&ocirc;ng c&oacute; thuyền, kh&ocirc;ng c&oacute; t&agrave;u vận tải vận chuyển h&agrave;ng ho&aacute; đi khắp thế giới từ những nơi xa x&ocirc;i của tr&aacute;i đất.</p>\r\n<p>Tr&ecirc;n thực tế, kh&ocirc;ng c&oacute; kế to&aacute;n, chắc hẳn bạn vẫn đang kiếm ăn hoặc săn bắn trong rừng</p>\r\n<p>Như bạn c&oacute; thể biết, chỉ c&oacute; hai lần đột ph&aacute; kế to&aacute;n trong to&agrave;n bộ lịch sử của thế giới trước giờ.</p>\r\n<p>Cả hai đều đ&atilde; tạo ra những nấc thang nhận thức to lớn, n&acirc;ng tầm t&iacute;nh phức tạp x&atilde; hội v&agrave; sự cải tiến của nh&acirc;n loại.</p>\r\n<p>Hầu hết mọi người đ&atilde; bỏ lỡ sự đột ph&aacute; của gi&aacute;o sư Ijiri v&igrave; n&oacute; nằm tr&ecirc;n hai lĩnh vực kh&aacute; mơ hồ v&agrave; c&ograve;n chưa phổ biến: mật m&atilde; v&agrave; kế to&aacute;n.</p>\r\n<p>Rất hiếm khi t&igrave;m được một người c&oacute; kinh nghiệm về một trong hai lĩnh vực đ&oacute;, chứ chưa n&oacute;i đến cả hai. Nếu kh&ocirc;ng c&oacute; sự hiểu biết li&ecirc;n ng&agrave;nh như vậy, kh&ocirc;ng c&oacute; g&igrave; ngạc nhi&ecirc;n khi ph&aacute;t minh của &ocirc;ng đ&atilde; tr&ocirc;i dần v&agrave;o l&atilde;ng qu&ecirc;n.</p>\r\n<p>V&agrave; c&oacute; một lĩnh vực m&agrave; &ocirc;ng ấy cũng đ&atilde; đi trước thời đại của m&igrave;nh. L&uacute;c đấy m&atilde; h&oacute;a vẫn c&ograve;n lạ lẫm với cộng đồng. Nếu bạn l&agrave;m việc trong ng&agrave;nh c&ocirc;ng nghệ th&ocirc;ng tin, bạn c&oacute; thể nhớ vụ scandal chip Clipper, nơi m&agrave; NSA (cơ quan t&igrave;nh b&aacute;o quốc gia Mỹ) đ&atilde; cố gắng c&agrave;i m&atilde; độc v&agrave;o trong tất cả c&aacute;c đoạn m&atilde;. Đ&oacute; l&agrave; chuyện của năm 1993. Ijiri xuất bản t&aacute;c phẩm của &ocirc;ng v&agrave;o năm 1989. N&oacute; đ&atilde; đi qua chừng đ&oacute; thời gian m&agrave; vẫn kh&ocirc;ng được ai ch&uacute; &yacute; đến.</p>\r\n<p>Sau đ&oacute;, v&agrave;o năm 2006/2007, một lập tr&igrave;nh vi&ecirc;n tự học đ&atilde; gi&aacute;ng một c&uacute; l&agrave;m rung chuyển cả hệ thống. Anh ấy đ&atilde; tạo ra một loại tiền tệ thay thế, mang t&iacute;nh phi tập trung (hay c&ograve;n gọi l&agrave; ph&acirc;n t&aacute;n).</p>\r\n<p><strong>N&oacute; được gọi l&agrave; Bitcoin.</strong></p>', '2022-05-31 09:10:16', 'user1', 3, 'images/3712.jpg', 'Chắc hẳn bạn chưa bao giờ từng nghe về Yuji Ijiri. Thế nhưng, vào năm 1989, ông đã tạo ra một thứ đáng kinh ngạc.'),
(23, '\"Sapiens: Lược sử loài người\": Bước đi trong vô định', '<div id=\"start_char_id\" class=\"start_char\">Mở đầu Sapien l&agrave; h&agrave;nh tr&igrave;nh của lo&agrave;i người ở thuở ban mai của nhận thức. Cuộc h&agrave;nh tr&igrave;nh bắt đầu bằng việc tiết lộ từ những b&iacute; mật động trời của lo&agrave;i người ch&uacute;ng ta: Homo Sapiens c&oacute; anh chị em, rằng ch&uacute;ng ta kh&ocirc;ng phải l&agrave; giống &ldquo;con người&rdquo; duy nhất tr&ecirc;n Tr&aacute;i đất hay Sapiens l&agrave; giống lo&agrave;i kh&ocirc;ng khoan dung, một tay che trời huỷ diệt gần như tất cả c&aacute;c tộc người kh&aacute;c, đến những b&iacute; mật kh&ocirc;ng mấy động trời như ch&uacute;ng ta l&agrave; lo&agrave;i săn bắt h&aacute;i lượm chứ kh&ocirc;ng sử dụng chảo để chi&ecirc;n, nu&ocirc;i lấy thịt, s&uacute;ng để săn như ng&agrave;y nay. T&aacute;c giả đ&atilde; vẽ n&ecirc;n một bức tranh to&agrave;n cảnh 70.000 năm qu&aacute; tr&igrave;nh tiến ho&aacute; của con người với sự nhấn mạnh ở ba cuộc c&aacute;ch mạng lớn: N&ocirc;ng Nghiệp, Nhận thức v&agrave; Khoa học. Mỗi cuộc c&aacute;ch mạng mang đến cho Sapiens sự chuyển biến r&otilde; rệt trong đời sống, trật tự x&atilde; hội v&agrave; khả năng tư duy, qua đ&oacute; cũng l&agrave;m bật l&ecirc;n được n&eacute;t ri&ecirc;ng biệt của lo&agrave;i Sapiens.</div>\r\n<p>Dĩ nhi&ecirc;n, nếu Sapiens của Yuval Harari chỉ đơn thuần l&agrave; một bản b&aacute;o c&aacute;o khoa học hơn năm trăm trang về từng bước đi của con người xuy&ecirc;n suốt 70.000 năm lịch sử th&igrave; sẽ l&agrave; một bất ngờ lớn khi Bill Gates khuyến kh&iacute;ch ch&uacute;ng ta&nbsp;<strong><em>mỗi người n&ecirc;n c&oacute; 1 quyển v&agrave; sau khi đọc xong, họ n&ecirc;n ngồi lại với những Homo Sapiens kh&aacute;c v&agrave; n&oacute;i về n&oacute;</em></strong>. Yuval Harari, với một nền tảng kiến thức s&acirc;u rộng trải d&agrave;i từ lịch sử thế giới, Trung cổ đến nh&acirc;n học, triết học, t&ocirc;n gi&aacute;o v&agrave; cả sự li&ecirc;n quan đến sinh học, đ&atilde; d&aacute;n đầy tr&ecirc;n những trang giấy trắng to&agrave;n chữ với chữ. Ch&iacute;nh điều n&agrave;y phần n&agrave;o cũng l&agrave;m nản ch&iacute; những độc giả của &ocirc;ng. Qu&aacute; nhiều kiến thức được ngưng tụ dưới dạng chữ viết. Nhưng ch&iacute;nh những kiến thức mang t&iacute;nh khoa học ấy lại l&agrave; nền tảng v&agrave; cơ sở để &ocirc;ng biện luận v&agrave; chứng minh cho những quan điểm rất ri&ecirc;ng của m&igrave;nh về cảnh quan chung của thế giới, xung quanh &ocirc;ng: từ ph&acirc;n biệt chủng tộc, nữ quyền cho đến bản chất của việc lần m&ograve; t&igrave;m kiếm hạnh ph&uacute;c.</p>\r\n<p><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/Sapiens%202.jpg\"></p>\r\n<p>Homo Sapiens - Một h&agrave;nh tr&igrave;nh đi xuy&ecirc;n suốt to&agrave;n bộ lịch sử của lo&agrave;i người</p>\r\n<p>&ldquo;Nhận thức&rdquo;, nghe thật đao to b&uacute;a lớn, nhưng thật vậy, ở những ng&agrave;y đầu sơ khai, con người đ&atilde; c&oacute; những bước tiến vĩ đại trong nhận thức. Chuyển m&igrave;nh từ lo&agrave;i đi bằng bốn ch&acirc;n hơi khom khom c&aacute;i lưng, bộ n&atilde;o của Sapiens to l&ecirc;n, mạng lưới noron ph&aacute;t triển phức tạp, lo&agrave;i người thuở bấy giờ ph&aacute;t hiện c&aacute;ch chế t&aacute;c ra lửa, x&acirc;y dựng n&ecirc;n một nền văn ho&aacute; biết &ldquo;ăn ch&iacute;n uống s&ocirc;i&rdquo;, sưởi ấm khi gi&aacute; r&eacute;t hay ph&oacute;ng hoả c&oacute; chủ đ&iacute;ch, tạo n&ecirc;n những đồng bằng xanh mới vốn tưởng đ&atilde; kh&ocirc; cằn &eacute;o &uacute;a. Với một nền tảng kiến thức tương đối, khả năng th&iacute;ch nghi, Sapiens l&uacute;c bấy giờ quyết định phi&ecirc;u bạt giang hồ, rời bỏ Ch&acirc;u Phi để đặt ch&acirc;n đến Ch&acirc;u &Acirc;u v&agrave; Đ&ocirc;ng Nam &Aacute;. V&agrave; tại đ&acirc;y, những sản phẩm handmade đầu ti&ecirc;n được ra đời. Trước l&agrave; ph&aacute;t minh ra t&agrave;u thuyền, đ&egrave;n dầu, cung t&ecirc;n v&agrave; kim kh&acirc;u, sau l&agrave; những đồ mỹ nghệ mang t&iacute;nh nghệ thuật cao như trang sức. Nhưng c&oacute; lẽ sản phẩm then chốt đ&atilde; tạo n&ecirc;n cuộc sống v&agrave; x&atilde; hội lo&agrave;i Sapiens hiện đại ch&uacute;ng ta ch&iacute;nh l&agrave; tưởng tượng c&ugrave;ng nhau.</p>\r\n<p>Tr&iacute; tưởng tượng được chắp c&aacute;nh bay xa. Mỗi ng&agrave;y sau khi l&agrave;m việc kiếm ăn lam lũ, Sapiens trở về nh&agrave; ngồi t&aacute;n d&oacute;c với nhau. Nội dung của những cuộc t&aacute;n d&oacute;c đ&atilde; trở th&agrave;nh những mối li&ecirc;n kết tạo n&ecirc;n một trật tự x&atilde; hội tưởng tượng với sự ph&acirc;n tầng giai cấp, với sự cả tin v&agrave;o những huyền thoại cổ t&iacute;ch.</p>', '2022-05-31 09:11:42', 'user1', 1, 'images/lichsu.jpg', 'Một hành trình đi xuyên suốt toàn bộ lịch sử của loài người, Homo Sapiens, được gói gọn trong 500 trang sách. Nhưng đó không chỉ là những lời văn lướt qua từng điểm đến lưu đầy dấu chân của Sapien, mà còn là chuyến đò trôi vào sâu tận nội tâm và cách nghĩ của loài người tinh khôn, hiện đại, chúng ta hay Homo Sapien.'),
(24, '2 cuốn sách kinh tế và quản trị của Alpha Books đạt Giải Sách Hay 2010 - 2020', '<p><strong>1. Cuộc Dịch Chuyển Đại Dương Xanh</strong></p>\r\n<p><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/123/5.jpg\"></p>\r\n<p>Cuốn s&aacute;ch Cuộc Dịch Chuyển Đại Dương Xanh (t&aacute;c giả W. Chan Kim, Ren&eacute;e Mauborgne, Dịch giả: Huỳnh Hữu T&agrave;i, Đơn vị xuất bản: Alpha Books &amp; NXB Lao động - X&atilde; hội) l&agrave; cuốn s&aacute;ch dịch đạt Giải S&aacute;ch Hay năm 2018. Năm 2005, Chan Kim &amp; Ren&eacute;e Mauborgne, hai gi&aacute;o sư nổi tiếng thế giới đ&atilde; thay đổi thế giới kinh doanh to&agrave;n cầu với cuốn s&aacute;ch \"Chiến Lược Đại Dương Xanh\" khi x&acirc;y dựng m&ocirc; h&igrave;nh &aacute;p dụng c&aacute;c chiến lược theo phương ph&aacute;p s&aacute;ng tạo, thay v&igrave; gi&agrave;nh giật thị trường. V&agrave; trong phi&ecirc;n bản nối tiếp của cuốn s&aacute;ch nền tảng n&agrave;y, Kim v&agrave; Mauborgne đ&atilde; chỉ ra những c&aacute;ch thức cụ thể để c&aacute;c c&ocirc;ng ty x&acirc;y dựng chiến lược Đại dương Xanh &ndash; nơi kh&ocirc;ng c&oacute; sự cạnh tranh khốc liệt từ đối thủ m&agrave; l&agrave; nơi c&oacute; những nhu cầu mới đặc trưng v&agrave; sự ph&aacute;t triển lợi nhuận mạnh mẽ.</p>\r\n<p><strong>2. Tư duy nhanh v&agrave; chậm</strong></p>\r\n<p><img src=\"http://300b5338.vws.vegacdn.vn/image/img.media/123/6.jpg\"></p>\r\n<p>Cuốn s&aacute;ch Tư duy nhanh v&agrave; chậm (t&aacute;c giả: Daniel Kahneman, Hương Lan &amp; Xu&acirc;n Thanh, NXB Thế giới &amp; Alpha Books) đạt Giải S&aacute;ch Hay năm 2019 thể loại s&aacute;ch dịch. Được cho l&agrave; &ldquo;kiệt t&aacute;c&rdquo; trong việc thay đổi h&agrave;nh vi, cuốn s&aacute;ch &ldquo;Tư duy nhanh v&agrave; chậm&rdquo; giới thiệu với độc giả 2 hệ th&ocirc;ng tư duy của con người: hệ thống tư duy nhanh v&agrave; hệ thống tư duy chậm; cũng như những ảnh hưởng của ch&uacute;ng tới c&aacute;c quyết định m&agrave; mỗi người thường đưa ra trong cuộc sống.</p>\r\n<p>Tư duy nhanh v&agrave; chậm đồng thời m&ocirc; tả những giới hạn trong tư duy, c&aacute;c nguy&ecirc;n tắc của ng&agrave;nh kinh tế học v&agrave; bản chất của việc đưa ra quyết định của con người; m&agrave; tất cả những người l&agrave;m kinh doanh đều c&oacute; thể nghi&ecirc;n cứu v&agrave; ứng dụng v&agrave;o c&ocirc;ng việc kinh doanh của m&igrave;nh.</p>', '2022-05-31 09:44:28', 'user1', 2, 'images/5.jpg', 'Lễ công bố Giải Sách Hay 2020 do Viện Giáo Dục IRED, Dự án Khuyến đọc Sách Hay và Sáng kiến OpenEdu đồng tổ chức đã diễn ra vào cuối tháng 9 vừa qua tại TP HCM. Trải qua 10 mùa giải, dù có nhiều thăng trầm, nhưng Giải Sách Hay đã tạo được tiếng vang đáng kể trong ngành sách, trở thành “bộ lọc” đáng tin cậy đối với rất nhiều độc giả yêu sách. Dưới đây là 2 cuốn sách kinh tế và quản trị đạt Giải Sách Hay hạng mục Sách Kinh tế và Sách Quản trị trong mùa giải năm nay.');

